CREATE PROC [dbo].[usp_RestaurantEmployeeRelations_Save]	@Id uniqueidentifier,	@RestaurantId uniqueidentifier,	@EmployeeId uniqueidentifier,	@EmployeeRoleId nvarchar(450),	@IsActive bit,	@CreatedAt datetime,	@CreatedBy nvarchar(100),	@UpdatedAt datetime,	@UpdatedBy nvarchar(100),	@DeletedAt datetime,	@DeletedBy nvarchar(100)ASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [dbo].[RestaurantEmployeeRelations] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [dbo].[RestaurantEmployeeRelations] (		[Id],		[RestaurantId],		[EmployeeId],		[EmployeeRoleId],		[IsActive],		[CreatedAt],		[CreatedBy],		[UpdatedAt],		[UpdatedBy],		[DeletedAt],		[DeletedBy]	)	VALUES (		@Id,		@RestaurantId,		@EmployeeId,		@EmployeeRoleId,		@IsActive,		@CreatedAt,		@CreatedBy,		@UpdatedAt,		@UpdatedBy,		@DeletedAt,		@DeletedBy	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE RestaurantEmployeeRelations SET 		[RestaurantId] = @RestaurantId,		[EmployeeId] = @EmployeeId,		[EmployeeRoleId] = @EmployeeRoleId,		[IsActive] = @IsActive,		[CreatedAt] = @CreatedAt,		[CreatedBy] = @CreatedBy,		[UpdatedAt] = @UpdatedAt,		[UpdatedBy] = @UpdatedBy,		[DeletedAt] = @DeletedAt,		[DeletedBy] = @DeletedBy	WHERE [Id] = @IdEND