
CREATE PROC [dbo].[usp_RestaurantMediaTags_Save]	@Id uniqueidentifier,	@RestaurantMediaId uniqueidentifier,	@TagId uniqueidentifier,	@IsActive bit,	@CreatedAt datetime,	@CreatedBy nvarchar(100),	@DeletedAt datetime,	@DeletedBy nvarchar(100)ASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [dbo].[RestaurantMediaTags] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [dbo].[RestaurantMediaTags] (		[Id],		[RestaurantMediaId],		[TagId],		[IsActive],		[CreatedAt],		[CreatedBy],		[DeletedAt],		[DeletedBy]	)	VALUES (		@Id,		@RestaurantMediaId,		@TagId,		@IsActive,		@CreatedAt,		@CreatedBy,		@DeletedAt,		@DeletedBy	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE RestaurantMediaTags SET 		[RestaurantMediaId] = @RestaurantMediaId,		[TagId] = @TagId,		[IsActive] = @IsActive,		[CreatedAt] = @CreatedAt,		[CreatedBy] = @CreatedBy,		[DeletedAt] = @DeletedAt,		[DeletedBy] = @DeletedBy	WHERE [Id] = @IdEND