CREATE PROC [dbo].[usp_RestaurantMedias_GetById]	@Id uniqueidentifierASSET NOCOUNT ONSELECT [Id], 	[RestaurantId], 	[MediaId], 	[DisplayOrder], 	[IsUploadedByRestaurant], 	[IsActive], 	[CreatedAt], 	[CreatedBy], 	[DeletedAt], 	[DeletedBy]FROM [dbo].[RestaurantMedias] WITH(NOLOCK)WHERE [Id] = @Id