CREATE PROC [dbo].[usp_RestaurantOperatingHours_GetById]	@Id uniqueidentifierASSET NOCOUNT ONSELECT [Id], 	[RestaurantId], 	[DayOfWeek], 	[OpeningTime], 	[ClosingTime], 	[FromDate], 	[ToDate], 	[IsClosed], 	[IsActive], 	[CreatedAt], 	[CreatedBy], 	[UpdatedAt], 	[UpdatedBy], 	[DeletedAt], 	[DeletedBy]FROM [dbo].[RestaurantOperatingHours] WITH(NOLOCK)WHERE [Id] = @Id