CREATE PROC [dbo].[usp_BrandMedias_Save]	@Id uniqueidentifier,	@BrandId uniqueidentifier,	@MediaId uniqueidentifier,	@DisplayOrder smallint,	@IsActive bit,	@CreatedAt datetime,	@CreatedBy nvarchar(100)ASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [dbo].[BrandMedias] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [dbo].[BrandMedias] (		[Id],		[BrandId],		[MediaId],		[DisplayOrder],		[IsActive],		[CreatedAt],		[CreatedBy]	)	VALUES (		@Id,		@BrandId,		@MediaId,		@DisplayOrder,		@IsActive,		@CreatedAt,		@CreatedBy	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE BrandMedias SET 		[BrandId] = @BrandId,		[MediaId] = @MediaId,		[DisplayOrder] = @DisplayOrder,		[IsActive] = @IsActive	WHERE [Id] = @IdEND