CREATE PROC [dbo].[usp_BrandOperatorRestaurants_Save]	@Id uniqueidentifier,	@BrandId uniqueidentifier,	@OperatorId uniqueidentifier,	@RestaurantId uniqueidentifier,	@StartDate datetime,	@EndDate datetime,	@Comments nvarchar(max),	@IsActive bit,	@CreatedAt datetime,	@CreatedBy nvarchar(100),	@UpdatedAt datetime,	@UpdatedBy nvarchar(100)ASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [dbo].[BrandOperatorRestaurants] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [dbo].[BrandOperatorRestaurants] (		[Id],		[BrandId],		[OperatorId],		[RestaurantId],		[StartDate],		[EndDate],		[Comments],		[IsActive],		[CreatedAt],		[CreatedBy]	)	VALUES (		@Id,		@BrandId,		@OperatorId,		@RestaurantId,		@StartDate,		@EndDate,		@Comments,		@IsActive,		@CreatedAt,		@CreatedBy	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE BrandOperatorRestaurants SET 		[BrandId] = @BrandId,		[OperatorId] = @OperatorId,		[RestaurantId] = @RestaurantId,		[StartDate] = @StartDate,		[EndDate] = @EndDate,		[Comments] = @Comments,		[IsActive] = @IsActive,		[UpdatedAt] = @UpdatedAt,		[UpdatedBy] = @UpdatedBy	WHERE [Id] = @IdEND