CREATE PROC [dbo].[usp_RestaurantTemporaryClosedDates_Save]	@Id uniqueidentifier,	@RestaurantId uniqueidentifier,	@FromDate date,	@ToDate date,	@IsActive bit,	@CreatedAt datetime,	@CreatedBy nvarchar(100),	@UpdatedAt datetime,	@UpdatedBy nvarchar(100),	@DeletedAt datetime,	@DeletedBy nvarchar(100)ASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [dbo].[RestaurantTemporaryClosedDates] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [dbo].[RestaurantTemporaryClosedDates] (		[Id],		[RestaurantId],		[FromDate],		[ToDate],		[IsActive],		[CreatedAt],		[CreatedBy],		[UpdatedAt],		[UpdatedBy],		[DeletedAt],		[DeletedBy]	)	VALUES (		@Id,		@RestaurantId,		@FromDate,		@ToDate,		@IsActive,		@CreatedAt,		@CreatedBy,		@UpdatedAt,		@UpdatedBy,		@DeletedAt,		@DeletedBy	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE RestaurantTemporaryClosedDates SET 		[RestaurantId] = @RestaurantId,		[FromDate] = @FromDate,		[ToDate] = @ToDate,		[IsActive] = @IsActive,		[CreatedAt] = @CreatedAt,		[CreatedBy] = @CreatedBy,		[UpdatedAt] = @UpdatedAt,		[UpdatedBy] = @UpdatedBy,		[DeletedAt] = @DeletedAt,		[DeletedBy] = @DeletedBy	WHERE [Id] = @IdEND