CREATE PROC [mst].[usp_AddressTypes_Save]	@Id uniqueidentifier,	@AddressType nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[AddressTypes] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[AddressTypes] (		[Id],		[AddressType],		[IsActive]	)	VALUES (		@Id,		@AddressType,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE AddressTypes SET 		[AddressType] = @AddressType,		[IsActive] = @IsActive	WHERE [Id] = @IdEND