CREATE PROC [mst].[usp_CancelReasons_Save]	@Id uniqueidentifier,	@CancelReason nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[CancelReasons] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[CancelReasons] (		[Id],		[CancelReason],		[IsActive]	)	VALUES (		@Id,		@CancelReason,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE CancelReasons SET 		[CancelReason] = @CancelReason,		[IsActive] = @IsActive	WHERE [Id] = @IdEND