CREATE PROC [mst].[usp_Countries_Save]	@Id uniqueidentifier,	@Name nvarchar(255),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[Countries] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[Countries] (		[Id],		[Name],		[IsActive]	)	VALUES (		@Id,		@Name,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE Countries SET 		[Name] = @Name,		[IsActive] = @IsActive	WHERE [Id] = @IdEND