CREATE PROC [mst].[usp_Cuisines_Save]	@Id uniqueidentifier,	@Cuisine nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[Cuisines] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[Cuisines] (		[Id],		[Cuisine],		[IsActive]	)	VALUES (		@Id,		@Cuisine,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE Cuisines SET 		[Cuisine] = @Cuisine,		[IsActive] = @IsActive	WHERE [Id] = @IdEND