CREATE PROC [mst].[usp_FoodTypes_Save]	@Id uniqueidentifier,	@FoodType nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[FoodTypes] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[FoodTypes] (		[Id],		[FoodType],		[IsActive]	)	VALUES (		@Id,		@FoodType,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE FoodTypes SET 		[FoodType] = @FoodType,		[IsActive] = @IsActive	WHERE [Id] = @IdEND