CREATE PROC [mst].[usp_MediaTypes_Save]	@Id uniqueidentifier,	@MediaType nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[MediaTypes] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[MediaTypes] (		[Id],		[MediaType],		[IsActive]	)	VALUES (		@Id,		@MediaType,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE MediaTypes SET 		[MediaType] = @MediaType,		[IsActive] = @IsActive	WHERE [Id] = @IdEND