CREATE PROC [mst].[usp_States_Save]	@Id uniqueidentifier,	@Name nvarchar(255),	@CountryId uniqueidentifier,	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[States] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[States] (		[Id],		[Name],		[CountryId],		[IsActive]	)	VALUES (		@Id,		@Name,		@CountryId,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE States SET 		[Name] = @Name,		[CountryId] = @CountryId,		[IsActive] = @IsActive	WHERE [Id] = @IdEND