CREATE PROC [mst].[usp_BookingStatuses_Save]	@Id uniqueidentifier,	@BookingStatus nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[BookingStatuses] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[BookingStatuses] (		[Id],		[BookingStatus],		[IsActive]	)	VALUES (		@Id,		@BookingStatus,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE BookingStatuses SET 		[BookingStatus] = @BookingStatus,		[IsActive] = @IsActive	WHERE [Id] = @IdEND