CREATE PROC [mst].[usp_SpecialOccasions_Save]	@Id uniqueidentifier,	@SpecialOccasion nvarchar(100),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[SpecialOccasions] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[SpecialOccasions] (		[Id],		[SpecialOccasion],		[IsActive]	)	VALUES (		@Id,		@SpecialOccasion,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE SpecialOccasions SET 		[SpecialOccasion] = @SpecialOccasion,		[IsActive] = @IsActive	WHERE [Id] = @IdEND