CREATE PROC [mst].[usp_Tags_Save]	@Id uniqueidentifier,	@Tag nvarchar(255),	@IsActive bitASSET NOCOUNT ONIF NOT EXISTS (SELECT * FROM [mst].[Tags] WITH(NOLOCK) WHERE [Id] = @Id) BEGIN	INSERT INTO [mst].[Tags] (		[Id],		[Tag],		[IsActive]	)	VALUES (		@Id,		@Tag,		@IsActive	)	SELECT SCOPE_IDENTITY() As InsertedIDENDELSE BEGIN	UPDATE Tags SET 		[Tag] = @Tag,		[IsActive] = @IsActive	WHERE [Id] = @IdEND